  }

  Future<void> attemptRestore() async {
    if (_formKey.currentState!.validate()) {
      if (mounted) setState(() => _hideSeedWords = true);

      String mnemonic = "";
      for (final element in _controllers) {
        mnemonic += " ${element.text.trim().toLowerCase()}";
      }
      mnemonic = mnemonic.trim();

      final int height = widget.restoreBlockHeight;
      String? otherDataJsonString;

      // TODO: make more robust estimate of date maybe using https://explorer.epic.tech/api-index
      if (widget.coin is Epiccash) {
        otherDataJsonString = jsonEncode({
          WalletInfoKeys.epiccashData: jsonEncode(
            ExtraEpiccashWalletInfo(
              receivingIndex: 0,
              changeIndex: 0,
              slatesToAddresses: {},
              slatesToCommits: {},
              lastScannedBlock: height,
              restoreHeight: height,
              creationHeight: height,
            ).toMap(),
          ),
        });
      }

      // TODO: do actual check to make sure it is a valid mnemonic for monero + xelis
      if (bip39.validateMnemonic(mnemonic) == false &&
          !(widget.coin is Monero ||
              widget.coin is Wownero ||
              widget.coin is Salvium ||
              widget.coin is Xelis)) {
        unawaited(
          showFloatingFlushBar(
            type: FlushBarType.warning,
            message: "Invalid seed phrase!",
            context: context,
          ),
        );
      } else {
        if (!Platform.isLinux) await WakelockPlus.enable();

        final info = WalletInfo.createNew(
          coin: widget.coin,
          name: widget.walletName,
          restoreHeight: height,
          otherDataJsonString: otherDataJsonString,
        );

        bool isRestoring = true;
        // show restoring in progress

        if (mounted) {
          unawaited(
            showDialog<dynamic>(
              context: context,
              useSafeArea: false,
              barrierDismissible: false,
              builder: (context) {
                return RestoringDialog(
                  onCancel: () async {
                    isRestoring = false;

                    if (mounted) setState(() => _hideSeedWords = false);

                    await ref
                        .read(pWallets)
                        .deleteWallet(info, ref.read(secureStoreProvider));
                  },
                );
              },
            ),
          );
        }

        var node = ref
            .read(nodeServiceChangeNotifierProvider)
            .getPrimaryNodeFor(currency: widget.coin);

        if (node == null) {
          node = widget.coin.defaultNode;
          await ref
              .read(nodeServiceChangeNotifierProvider)
              .setPrimaryNodeFor(coin: widget.coin, node: node);
        }

        final txTracker = TransactionNotificationTracker(
          walletId: info.walletId,
        );

        try {
          final wallet = await Wallet.create(
            walletInfo: info,
            mainDB: ref.read(mainDBProvider),
            secureStorageInterface: ref.read(secureStoreProvider),
            nodeService: ref.read(nodeServiceChangeNotifierProvider),
            prefs: ref.read(prefsChangeNotifierProvider),
            mnemonicPassphrase: widget.mnemonicPassphrase,
            mnemonic: mnemonic,
          );

          // TODO: extract interface with isRestore param
          switch (wallet.runtimeType) {
            case const (EpiccashWallet):
              await (wallet as EpiccashWallet).init(isRestore: true);
              break;

            case const (MoneroWallet):
              await (wallet as MoneroWallet).init(isRestore: true);
              break;

            case const (WowneroWallet):
              await (wallet as WowneroWallet).init(isRestore: true);
              break;

            case const (SalviumWallet):
              await (wallet as SalviumWallet).init(isRestore: true);
              break;

            case const (XelisWallet):
              await (wallet as XelisWallet).init(isRestore: true);
              break;

            default:
              await wallet.init();
          }

          await wallet.recover(isRescan: false);

          if (wallet is ExternalWallet) {
            await wallet.exit();
          }

          // check if state is still active before continuing
          if (mounted) {
            await wallet.info.setMnemonicVerified(
              isar: ref.read(mainDBProvider).isar,
            );

            if (ref.read(pDuress)) {
              await wallet.info.updateDuressVisibilityStatus(
                isDuressVisible: true,
                isar: ref.read(mainDBProvider).isar,
              );
            }

            ref.read(pWallets).addWallet(wallet);

            final isCreateSpecialEthWallet = ref.read(
              createSpecialEthWalletRoutingFlag,
            );
            if (isCreateSpecialEthWallet) {
              ref.read(createSpecialEthWalletRoutingFlag.notifier).state =
                  false;
              ref
                  .read(newEthWalletTriggerTempUntilHiveCompletelyDeleted.state)
                  .state = !ref
                      .read(
                        newEthWalletTriggerTempUntilHiveCompletelyDeleted.state,
                      )
                      .state;
            }

            if (mounted) {
              if (isDesktop) {
                Navigator.of(
                  context,
                ).popUntil(ModalRoute.withName(DesktopHomeView.routeName));
              } else {
                if (isCreateSpecialEthWallet) {
                  Navigator.of(context).popUntil(
                    ModalRoute.withName(SelectWalletForTokenView.routeName),
                  );
                } else {
                  unawaited(
                    Navigator.of(context).pushNamedAndRemoveUntil(
                      HomeView.routeName,
                      (route) => false,
                    ),
                  );
                  if (info.coin is Ethereum) {
                    unawaited(
                      Navigator.of(context).pushNamed(
                        EditWalletTokensView.routeName,
                        arguments: wallet.walletId,
                      ),
                    );
                  }
                }
              }

              await showDialog<dynamic>(
                context: context,
                useSafeArea: false,
                barrierDismissible: true,
                builder: (context) {
                  return const RestoreSucceededDialog();
                },
              );
            }

            if (!Platform.isLinux && !isDesktop) {
              await WakelockPlus.disable();
            }
          }
        } catch (e) {
          if (!Platform.isLinux && !isDesktop) {
            await WakelockPlus.disable();
          }

          // if (e is HiveError &&
          //     e.message == "Box has already been closed.") {
          //   // restore was cancelled
          //   return;
          // }

          // check if state is still active and restore wasn't cancelled
          // before continuing
          if (mounted && isRestoring) {
            // pop waiting dialog
            Navigator.pop(context);

            // show restoring wallet failed dialog
            await showDialog<dynamic>(
              context: context,
              useSafeArea: false,
              barrierDismissible: true,
              builder: (context) {
                return RestoreFailedDialog(
                  errorMessage: e.toString(),
                  walletId: info.walletId,
                  walletName: info.name,
                );
              },
            );

            if (mounted) setState(() => _hideSeedWords = false);
          }
        }

        if (!Platform.isLinux && !isDesktop) {
          await WakelockPlus.disable();
        }
      }
    }
  }

  InputDecoration _getInputDecorationFor(
    FormInputStatus status,
    String prefix,
  ) {
    Color color;
    Color prefixColor;
    Color borderColor;
    Widget? suffixIcon;
    switch (status) {
      case FormInputStatus.empty:
        color = Theme.of(context).extension<StackColors>()!.textFieldDefaultBG;
        prefixColor = Theme.of(context).extension<StackColors>()!.textSubtitle2;
        borderColor =
            Theme.of(context).extension<StackColors>()!.textFieldDefaultBG;
        break;
      case FormInputStatus.invalid:
        color = Theme.of(context).extension<StackColors>()!.textFieldErrorBG;
        prefixColor =
            Theme.of(
              context,
            ).extension<StackColors>()!.textFieldErrorSearchIconLeft;
        borderColor =
            Theme.of(context).extension<StackColors>()!.textFieldErrorBorder;
        suffixIcon = SvgPicture.asset(
          Assets.svg.alertCircle,
          width: 16,
          height: 16,
          color:
              Theme.of(
                context,
              ).extension<StackColors>()!.textFieldErrorSearchIconRight,
        );
        break;
      case FormInputStatus.valid:
        color = Theme.of(context).extension<StackColors>()!.textFieldSuccessBG;
        prefixColor =
            Theme.of(
              context,
            ).extension<StackColors>()!.textFieldSuccessSearchIconLeft;
        borderColor =
            Theme.of(context).extension<StackColors>()!.textFieldSuccessBorder;
        suffixIcon = SvgPicture.asset(
          Assets.svg.checkCircle,
          width: 16,
          height: 16,
          color:
              Theme.of(
                context,
              ).extension<StackColors>()!.textFieldSuccessSearchIconRight,
        );
        break;
    }
    return InputDecoration(
      fillColor: color,
      filled: true,
      contentPadding: const EdgeInsets.symmetric(vertical: 12, horizontal: 16),
      prefixIcon: Align(
        alignment: Alignment.centerLeft,
        child: Padding(
          padding: const EdgeInsets.only(left: 12, bottom: 2),
          child: Text(
            prefix,
            style: STextStyles.fieldLabel(
              context,
            ).copyWith(color: prefixColor, fontSize: Util.isDesktop ? 16 : 14),
          ),
        ),
      ),
      prefixIconConstraints: const BoxConstraints(
        minWidth: 16,
        minHeight: 16,
        maxWidth: 36,
        maxHeight: 32,
      ),
      suffixIconConstraints: const BoxConstraints(
        minWidth: 16,
        minHeight: 16,
        maxWidth: 28,
        maxHeight: 16,
      ),
      suffixIcon: Center(
        child: Padding(
          padding: const EdgeInsets.only(right: 12),
          child: suffixIcon,
        ),
      ),
      enabledBorder: _buildOutlineInputBorder(borderColor),
      focusedBorder: _buildOutlineInputBorder(borderColor),
      errorBorder: _buildOutlineInputBorder(borderColor),
      disabledBorder: _buildOutlineInputBorder(borderColor),
      focusedErrorBorder: _buildOutlineInputBorder(borderColor),
    );
  }

  void _clearAndPopulateMnemonic(List<String> words) {
    final count = min(_controllers.length, words.length);

    // replace field content with listed words
    for (int i = 0; i < count; i++) {
      final word = words[i].trim();
      _controllers[i].text = words[i];
      if (_isValidMnemonicWord(word.toLowerCase())) {
        setState(() {
          _inputStatuses[i] = FormInputStatus.valid;
        });
      } else {
        setState(() {
          _inputStatuses[i] = FormInputStatus.invalid;
        });
      }
    }

    // clear remaining fields
    for (int i = count; i < _controllers.length; i++) {
      _controllers[i].text = "";
      setState(() {
        _inputStatuses[i] = FormInputStatus.empty;
      });
    }

    if (!isDesktop) {
      controller.animateTo(
        controller.position.maxScrollExtent,
        duration: const Duration(milliseconds: 300),
        curve: Curves.decelerate,
      );
    }
  }

  Future<void> scanMnemonicQr() async {
    try {
      final qrResult = await ref.read(pBarcodeScanner).scan();

      final results = AddressUtils.decodeQRSeedData(qrResult.rawContent);

      if (results["mnemonic"] != null) {
        final list = (results["mnemonic"] as List)
            .map((value) => value as String)
            .toList(growable: false);
        if (list.isNotEmpty) {
          _clearAndPopulateMnemonic(list);
          Logging.instance.i("mnemonic populated");
        } else {
          Logging.instance.i("mnemonic failed to populate");
        }
      }
    } on PlatformException catch (e, s) {
      // likely failed to get camera permissions
      if (mounted) {
        try {
          await checkCamPermDeniedMobileAndOpenAppSettings(
            context,
            logging: Logging.instance,
          );
        } catch (e, s) {
          Logging.instance.e(
            "Failed to check cam permissions",
            error: e,
            stackTrace: s,
          );
        }
      } else {
        Logging.instance.e(
          "Restore wallet qr scan failed: $e",
          error: e,
          stackTrace: s,
        );
      }
    }
  }

  Future<void> pasteMnemonic() async {
    //todo: check if print needed
    // debugPrint("restoreWalletPasteButton tapped");
    final ClipboardData? data = await widget.clipboard.getData(
      Clipboard.kTextPlain,
    );

    if (data?.text != null && data!.text!.isNotEmpty) {
      final content = data.text!.trim();
      final list = content.split(" ");
      _clearAndPopulateMnemonic(list);
    }
  }

  Future<void> requestRestore() async {
    // wait for keyboard to disappear
    FocusScope.of(context).unfocus();
    await Future<void>.delayed(const Duration(milliseconds: 100));

    if (mounted) {
      await showDialog<dynamic>(
        context: context,
        useSafeArea: false,
        barrierDismissible: true,
        builder: (context) {
          return ConfirmRecoveryDialog(onConfirm: attemptRestore);
        },
      );
    }
  }

