  Future<void> _attemptSend(BuildContext context) async {
    final wallet = ref.read(pWallets).getWallet(walletId);
    final coin = wallet.info.coin;

    final sendProgressController = ProgressAndSuccessController();

    unawaited(
      showDialog<void>(
        context: context,
        useSafeArea: false,
        barrierDismissible: false,
        builder: (context) {
          return SendingTransactionDialog(
            coin: coin,
            controller: sendProgressController,
          );
        },
      ),
    );

    final time = Future<dynamic>.delayed(const Duration(milliseconds: 2500));

    late String txid;
    Future<TxData> txidFuture;

    final String note = widget.txData.note ?? "";

    try {
      if (wallet is FiroWallet && widget.shouldSendPublicFiroFunds == false) {
        txidFuture = wallet.confirmSendSpark(txData: widget.txData);
      } else {
        txidFuture = wallet.confirmSend(txData: widget.txData);
      }

      unawaited(wallet.refresh());

      final results = await Future.wait([txidFuture, time]);

      sendProgressController.triggerSuccess?.call();
      await Future<void>.delayed(const Duration(seconds: 5));

      txid = (results.first as TxData).txid!;

      // save note
      await ref
          .read(mainDBProvider)
          .putTransactionNote(
            TransactionNote(walletId: walletId, txid: txid, value: note),
          );

      await ref
          .read(tradeSentFromStackLookupProvider)
          .save(
            tradeWalletLookup: TradeWalletLookup(
              uuid: const Uuid().v1(),
              txid: txid,
              tradeId: trade.tradeId,
              walletIds: [walletId],
            ),
          );

      // pop back to wallet
      if (context.mounted) {
        if (Util.isDesktop) {
          // pop sending dialog
          Navigator.of(context, rootNavigator: true).pop();

          // one day we'll do routing right
          Navigator.of(context, rootNavigator: true).pop();
          if (widget.fromDesktopStep4) {
            Navigator.of(context, rootNavigator: true).pop();
          }
        }

        Navigator.of(context).popUntil(ModalRoute.withName(routeOnSuccessName));
      }
    } catch (e, s) {
      Logging.instance.e(
        "Broadcast transaction failed: ",
        error: e,
        stackTrace: s,
      );

      // pop sending dialog
      Navigator.of(context).pop();

      await showDialog<dynamic>(
        context: context,
        useSafeArea: false,
        barrierDismissible: true,
        builder: (context) {
          return StackDialog(
            title: "Broadcast transaction failed",
            message: e.toString(),
            rightButton: TextButton(
              style: Theme.of(context)
                  .extension<StackColors>()!
                  .getSecondaryEnabledButtonStyle(context),
              child: Text(
                "Ok",
                style: STextStyles.button(context).copyWith(
                  color:
                      Theme.of(
                        context,
                      ).extension<StackColors>()!.buttonTextSecondary,
                ),
              ),
              onPressed: () {
                Navigator.of(context).pop();
              },
            ),
          );
        },
      );
    }
  }

  Future<void> _confirmSend() async {
    final dynamic unlocked;

    final coin = ref.read(pWalletCoin(walletId));

    if (Util.isDesktop) {
      unlocked = await showDialog<bool?>(
        context: context,
        builder:
            (context) => DesktopDialog(
              maxWidth: 580,
              maxHeight: double.infinity,
              child: Column(
                mainAxisSize: MainAxisSize.min,
                children: [
                  const Row(
                    mainAxisAlignment: MainAxisAlignment.end,
                    children: [DesktopDialogCloseButton()],
                  ),
                  Padding(
                    padding: const EdgeInsets.only(
                      left: 32,
                      right: 32,
                      bottom: 32,
                    ),
                    child: DesktopAuthSend(coin: coin),
                  ),
                ],
              ),
            ),
      );
    } else {
      unlocked = await Navigator.push(
        context,
        RouteGenerator.getRoute(
          shouldUseMaterialRoute: RouteGenerator.useMaterialPageRoute,
          builder:
              (_) => const LockscreenView(
                showBackButton: true,
                popOnSuccess: true,
                routeOnSuccessArguments: true,
                routeOnSuccess: "",
                biometricsCancelButtonString: "CANCEL",
                biometricsLocalizedReason: "Authenticate to send transaction",
                biometricsAuthenticationTitle: "Confirm Transaction",
              ),
          settings: const RouteSettings(name: "/confirmsendlockscreen"),
        ),
      );
    }

    if (unlocked is bool && mounted) {
      if (unlocked) {
        await _attemptSend(context);
      } else {
        unawaited(
          showFloatingFlushBar(
            type: FlushBarType.warning,
            message: "Invalid passphrase",
            context: context,
          ),
        );
      }
    }
  }

