class SendFromCard extends ConsumerStatefulWidget {
  const SendFromCard({
    super.key,
    required this.walletId,
    required this.amount,
    required this.address,
    required this.trade,
    this.fromDesktopStep4 = false,
  });

  final String walletId;
  final Amount amount;
  final String address;
  final Trade trade;
  final bool fromDesktopStep4;

  @override
  ConsumerState<SendFromCard> createState() => _SendFromCardState();
}

class _SendFromCardState extends ConsumerState<SendFromCard> {
  late final String walletId;
  late final Amount amount;
  late final String address;
  late final Trade trade;

  Future<void> _send({bool? shouldSendPublicFiroFunds}) async {
    final coin = ref.read(pWalletCoin(walletId));

    try {
      bool wasCancelled = false;

      final wallet = ref.read(pWallets).getWallet(walletId);

      unawaited(
        showDialog<dynamic>(
          context: context,
          useSafeArea: false,
          barrierDismissible: false,
          builder: (context) {
            return ConditionalParent(
              condition: Util.isDesktop,
              builder:
                  (child) => DesktopDialog(
                    maxWidth: 400,
                    maxHeight: double.infinity,
                    child: Padding(
                      padding: const EdgeInsets.all(32),
                      child: child,
                    ),
                  ),
              child: BuildingTransactionDialog(
                coin: coin,
                isSpark:
                    wallet is FiroWallet && shouldSendPublicFiroFunds != true,
                onCancel: () {
                  wasCancelled = true;

                  Navigator.of(context).pop();
                },
              ),
            );
          },
        ),
      );

      // Currently CwBasedInterface wallets (xmr/wow) shouldn't even have
      // access to this screen but this is needed to get past an error that
      // would occur only to lead to another error which is why xmr/wow wallets
      // don't have access to this screen currently
      if (wallet is ExternalWallet) {
        await wallet.init();
        await wallet.open();
      }

      final time = Future<dynamic>.delayed(const Duration(milliseconds: 2500));

      TxData txData;
      Future<TxData> txDataFuture;

      final recipient = TxRecipient(
        address: address,
        amount: amount,
        isChange: false,
        addressType: wallet.cryptoCurrency.getAddressType(address)!,
      );

      // if not firo then do normal send
      if (shouldSendPublicFiroFunds == null) {
        final memo =
            coin is Stellar
                ? trade.payInExtraId.isNotEmpty
                    ? trade.payInExtraId
                    : null
                : null;
        txDataFuture = wallet.prepareSend(
          txData: TxData(
            recipients: [recipient],
            memo: memo,
            feeRateType: FeeRateType.average,
          ),
        );
      } else {
        final firoWallet = wallet as FiroWallet;
        // otherwise do firo send based on balance selected
        if (shouldSendPublicFiroFunds) {
          txDataFuture = wallet.prepareSend(
            txData: TxData(
              recipients: [recipient],
              feeRateType: FeeRateType.average,
            ),
          );
        } else {
          txDataFuture = firoWallet.prepareSendSpark(
            txData: TxData(
              recipients: [recipient],
              // feeRateType: FeeRateType.average,
            ),
          );
        }
      }

      final results = await Future.wait([txDataFuture, time]);

      txData = results.first as TxData;

      if (!wasCancelled) {
        // pop building dialog

        if (mounted) {
          Navigator.of(context, rootNavigator: Util.isDesktop).pop();
        }

        txData = txData.copyWith(
          note:
              "${trade.payInCurrency.toUpperCase()}/"
              "${trade.payOutCurrency.toUpperCase()} exchange",
        );

        if (mounted) {
          await Navigator.of(context).push(
            RouteGenerator.getRoute(
              shouldUseMaterialRoute: RouteGenerator.useMaterialPageRoute,
              builder:
                  (_) => ConfirmChangeNowSendView(
                    txData: txData,
                    walletId: walletId,
                    routeOnSuccessName:
                        Util.isDesktop
                            ? DesktopExchangeView.routeName
                            : HomeView.routeName,
                    trade: trade,
                    shouldSendPublicFiroFunds: shouldSendPublicFiroFunds,
                    fromDesktopStep4: widget.fromDesktopStep4,
                  ),
              settings: const RouteSettings(
                name: ConfirmChangeNowSendView.routeName,
              ),
            ),
          );
        }
      }
    } catch (e, s) {
      Logging.instance.e("$e\n$s", error: e, stackTrace: s);
      if (mounted) {
        // pop building dialog
        Navigator.of(context).pop();

        await showDialog<dynamic>(
          context: context,
          useSafeArea: false,
          barrierDismissible: true,
          builder: (context) {
            return StackDialog(
              title: "Transaction failed",
              message: e.toString(),
              rightButton: TextButton(
                style: Theme.of(context)
                    .extension<StackColors>()!
                    .getSecondaryEnabledButtonStyle(context),
                child: Text(
                  "Ok",
                  style: STextStyles.button(context).copyWith(
                    color:
                        Theme.of(
                          context,
                        ).extension<StackColors>()!.buttonTextSecondary,
                  ),
                ),
                onPressed: () {
                  Navigator.of(context).pop();
                },
              ),
            );
          },
        );
      }
    }
  }

  @override
  void initState() {
    walletId = widget.walletId;
    amount = widget.amount;
    address = widget.address;
    trade = widget.trade;
    super.initState();
  }

  @override
  Widget build(BuildContext context) {
    final coin = ref.watch(pWalletCoin(walletId));

    final isFiro = coin is Firo;

    return RoundedWhiteContainer(
      padding: const EdgeInsets.all(0),
      child: ConditionalParent(
        condition: isFiro,
        builder:
            (child) => Expandable(
              header: Container(
                color: Colors.transparent,
                child: Padding(padding: const EdgeInsets.all(12), child: child),
              ),
              body: Column(
                mainAxisSize: MainAxisSize.min,
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  if (!trade.exchangeName.startsWith(
                    TrocadorExchange.exchangeName,
                  ))
                    MaterialButton(
                      splashColor:
                          Theme.of(context).extension<StackColors>()!.highlight,
                      key: Key(
                        "walletsSheetItemButtonFiroPrivateKey_$walletId",
                      ),
                      padding: const EdgeInsets.all(0),
                      materialTapTargetSize: MaterialTapTargetSize.shrinkWrap,
                      shape: RoundedRectangleBorder(
                        borderRadius: BorderRadius.circular(
                          Constants.size.circularBorderRadius,
                        ),
                      ),
                      onPressed: () async {
                        if (mounted) {
                          unawaited(_send(shouldSendPublicFiroFunds: false));
                        }
                      },
                      child: Container(
                        color: Colors.transparent,
                        child: Padding(
                          padding: const EdgeInsets.only(
                            top: 6,
                            left: 16,
                            right: 16,
                            bottom: 6,
                          ),
                          child: Row(
                            mainAxisAlignment: MainAxisAlignment.spaceBetween,
                            children: [
                              Column(
                                mainAxisSize: MainAxisSize.min,
                                crossAxisAlignment: CrossAxisAlignment.start,
                                children: [
                                  Text(
                                    "Use private balance",
                                    style: STextStyles.itemSubtitle(context),
                                  ),
                                  Text(
                                    ref
                                        .watch(pAmountFormatter(coin))
                                        .format(
                                          ref
                                              .watch(
                                                pWalletBalanceTertiary(
                                                  walletId,
                                                ),
                                              )
                                              .spendable,
                                        ),
                                    style: STextStyles.itemSubtitle(context),
                                  ),
                                ],
                              ),
                              SvgPicture.asset(
                                Assets.svg.chevronRight,
                                height: 14,
                                width: 7,
                                color:
                                    Theme.of(
                                      context,
                                    ).extension<StackColors>()!.infoItemLabel,
                              ),
                            ],
                          ),
                        ),
                      ),
                    ),
                  MaterialButton(
                    splashColor:
                        Theme.of(context).extension<StackColors>()!.highlight,
                    key: Key("walletsSheetItemButtonFiroPublicKey_$walletId"),
                    padding: const EdgeInsets.all(0),
                    materialTapTargetSize: MaterialTapTargetSize.shrinkWrap,
                    shape: RoundedRectangleBorder(
                      borderRadius: BorderRadius.circular(
                        Constants.size.circularBorderRadius,
                      ),
                    ),
                    onPressed: () async {
                      if (mounted) {
                        unawaited(_send(shouldSendPublicFiroFunds: true));
                      }
                    },
                    child: Container(
                      color: Colors.transparent,
                      child: Padding(
                        padding: const EdgeInsets.only(
                          top: 6,
                          left: 16,
                          right: 16,
                          bottom: 6,
                        ),
                        child: Row(
                          mainAxisAlignment: MainAxisAlignment.spaceBetween,
                          children: [
                            Column(
                              mainAxisSize: MainAxisSize.min,
                              crossAxisAlignment: CrossAxisAlignment.start,
                              children: [
                                Text(
                                  "Use public balance",
                                  style: STextStyles.itemSubtitle(context),
                                ),
                                Text(
                                  ref
                                      .watch(pAmountFormatter(coin))
                                      .format(
                                        ref
                                            .watch(pWalletBalance(walletId))
                                            .spendable,
                                      ),
                                  style: STextStyles.itemSubtitle(context),
                                ),
                              ],
                            ),
                            SvgPicture.asset(
                              Assets.svg.chevronRight,
                              height: 14,
                              width: 7,
                              color:
                                  Theme.of(
                                    context,
                                  ).extension<StackColors>()!.infoItemLabel,
                            ),
                          ],
                        ),
                      ),
                    ),
                  ),
                  const SizedBox(height: 6),
                ],
              ),
            ),
        child: ConditionalParent(
          condition: !isFiro,
          builder:
              (child) => MaterialButton(
                splashColor:
                    Theme.of(context).extension<StackColors>()!.highlight,
                key: Key("walletsSheetItemButtonKey_$walletId"),
                padding: const EdgeInsets.all(8),
                materialTapTargetSize: MaterialTapTargetSize.shrinkWrap,
                shape: RoundedRectangleBorder(
                  borderRadius: BorderRadius.circular(
                    Constants.size.circularBorderRadius,
                  ),
                ),
                onPressed: () async {
                  if (mounted) {
                    unawaited(_send());
                  }
                },
                child: child,
              ),
          child: Row(
            children: [
              Container(
                decoration: BoxDecoration(
                  color: ref.watch(pCoinColor(coin)).withOpacity(0.5),
                  borderRadius: BorderRadius.circular(
                    Constants.size.circularBorderRadius,
                  ),
                ),
                child: Padding(
                  padding: const EdgeInsets.all(6),
                  child: SvgPicture.file(
                    File(ref.watch(coinIconProvider(coin))),
                    width: 24,
                    height: 24,
                  ),
                ),
              ),
              const SizedBox(width: 12),
              Expanded(
                child: Column(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: [
                    Text(
                      ref.watch(pWalletName(walletId)),
                      style: STextStyles.titleBold12(context),
                    ),
                    if (!isFiro) const SizedBox(height: 2),
                    if (!isFiro)
                      Text(
                        ref
                            .watch(pAmountFormatter(coin))
