            onChanged: (newValue) {
              if (widget.coin == Coin.epicCash) {
                if (newValue.startsWith("https://")) {
                  _useSSL = true;
                  enableSSLCheckbox = false;
                } else if (newValue.startsWith("http://")) {
                  _useSSL = false;
                  enableSSLCheckbox = false;
                } else {
                  enableSSLCheckbox = true;
                }
              }
              _updateState();
              setState(() {});
