class WalletBackupView extends ConsumerWidget {
  const WalletBackupView({
    super.key,
    required this.walletId,
    required this.mnemonic,
    this.frostWalletData,
    this.keyData,
  });

  static const String routeName = "/walletBackup";

  final String walletId;
  final List<String> mnemonic;
  final ({
    String myName,
    String config,
    String keys,
    ({String config, String keys})? prevGen,
  })?
  frostWalletData;
  final KeyDataInterface? keyData;

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    debugPrint("BUILD: $runtimeType");

    final bool frost = frostWalletData != null;

    return Background(
      child: Scaffold(
        backgroundColor: Theme.of(context).extension<StackColors>()!.background,
        appBar: AppBar(
          leading: AppBarBackButton(
            onPressed: () {
              Navigator.of(context).pop();
            },
          ),
          title: Text("Wallet backup", style: STextStyles.navBarTitle(context)),
          actions: [
            if (keyData != null)
              Padding(
                padding: const EdgeInsets.all(10),
                child: CustomTextButton(
                  text: switch (keyData) {
                    final XPrivData _ => "xpriv(s)",
                    final CWKeyData _ => "keys",
                    final ViewOnlyWalletData _ => "keys",
                    _ =>
                      throw UnimplementedError(
                        "Don't forget to add your KeyDataInterface here! ${keyData.runtimeType}",
                      ),
                  },
                  onTap: () {
                    Navigator.pushNamed(
                      context,
                      MobileKeyDataView.routeName,
                      arguments: (walletId: walletId, keyData: keyData!),
                    );
                  },
                ),
              ),
          ],
        ),
        body: SafeArea(
          child: Padding(
            padding: const EdgeInsets.all(16),
            child:
                frost
                    ? _FrostKeys(
                      frostWalletData: frostWalletData,
                      walletId: walletId,
                    )
                    : _Mnemonic(walletId: walletId, mnemonic: mnemonic),
          ),
        ),
      ),
    );
  }
}

class _Mnemonic extends ConsumerWidget {
  const _Mnemonic({
    super.key,
    required this.walletId,
    required this.mnemonic,
    this.clipboardInterface = const ClipboardWrapper(),
  });

  final String walletId;
  final List<String> mnemonic;
  final ClipboardInterface clipboardInterface;

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    return Column(
      crossAxisAlignment: CrossAxisAlignment.stretch,
      children: [
        Text(
          ref.watch(pWalletName(walletId)),
          textAlign: TextAlign.center,
          style: STextStyles.label(context).copyWith(fontSize: 12),
        ),
        const SizedBox(height: 4),
        Text(
          "Recovery Phrase",
          textAlign: TextAlign.center,
          style: STextStyles.pageTitleH1(context),
        ),
        const SizedBox(height: 16),
        Container(
          decoration: BoxDecoration(
            color: Theme.of(context).extension<StackColors>()!.popupBG,
            borderRadius: BorderRadius.circular(
              Constants.size.circularBorderRadius,
            ),
          ),
          child: Padding(
            padding: const EdgeInsets.all(12),
            child: Text(
              "Please write down your backup key. Keep it safe and never share "
              "it with anyone. Your backup key is the only way you can access"
              " your funds if you forget your PIN, lose your phone, etc.\n\n"
              "${AppConfig.appName} does not keep nor is able to restore your"
              " backup key. Only you have access to your wallet.",
              style: STextStyles.label(context),
            ),
          ),
        ),
        const SizedBox(height: 8),
        Expanded(
          child: SingleChildScrollView(
            child: MnemonicTable(words: mnemonic, isDesktop: false),
          ),
        ),
        const SizedBox(height: 12),
        SecondaryButton(
          label: "Copy",
          onPressed: () async {
            await clipboardInterface.setData(
              ClipboardData(text: mnemonic.join(" ")),
            );
            if (context.mounted) {
              unawaited(
                showFloatingFlushBar(
                  type: FlushBarType.info,
                  message: "Copied to clipboard",
                  iconAsset: Assets.svg.copy,
                  context: context,
                ),
              );
            }
          },
        ),
        const SizedBox(height: 12),
        PrimaryButton(
          label: "Show QR Code",
          onPressed: () {
            final String data = AddressUtils.encodeQRSeedData(mnemonic);

            showDialog<dynamic>(
              context: context,
              useSafeArea: false,
              barrierDismissible: true,
              builder: (_) {
                final width = MediaQuery.of(context).size.width / 2;
                return StackDialogBase(
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.stretch,
                    children: [
                      Center(
                        child: Text(
                          "Recovery phrase QR code",
                          style: STextStyles.pageTitleH2(context),
                        ),
                      ),
                      const SizedBox(height: 12),
                      Center(
                        child: RepaintBoundary(
                          // key: _qrKey,
                          child: SizedBox(
                            width: width + 20,
                            height: width + 20,
                            child: QR(data: data, size: width),
                          ),
                        ),
                      ),
                      const SizedBox(height: 12),
                      Center(
                        child: SizedBox(
                          width: width,
                          child: TextButton(
                            onPressed: () async {
                              // await _capturePng(true);
                              Navigator.of(context).pop();
                            },
                            style: Theme.of(context)
                                .extension<StackColors>()!
                                .getSecondaryEnabledButtonStyle(context),
                            child: Text(
                              "Cancel",
                              style: STextStyles.button(context).copyWith(
                                color:
                                    Theme.of(
                                      context,
                                    ).extension<StackColors>()!.accentColorDark,
                              ),
                            ),
                          ),
                        ),
                      ),
                    ],
                  ),
                );
              },
            );
          },
        ),
      ],
    );
  }
}

