            style: Theme.of(context)
                .extension<StackColors>()!
                .getPrimaryEnabledButtonStyle(context),
            onPressed: () async {
              await ref.read(pWallets).deleteWallet(
                    ref.read(pWalletInfo(widget.walletId)),
                    ref.read(secureStoreProvider),
