class TokenSummary extends ConsumerWidget {
  const TokenSummary({
    super.key,
    required this.walletId,
    required this.initialSyncStatus,
  });

  final String walletId;
  final WalletSyncStatus initialSyncStatus;

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final token =
        ref.watch(pCurrentTokenWallet.select((value) => value!.tokenContract));
    final balance = ref.watch(
      pTokenBalance((walletId: walletId, contractAddress: token.address)),
    );

    return Stack(
      children: [
        RoundedContainer(
          color: Theme.of(context).extension<StackColors>()!.tokenSummaryBG,
          padding: const EdgeInsets.all(24),
          child: Column(
            children: [
              Row(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  SvgPicture.asset(
                    Assets.svg.walletDesktop,
                    color: Theme.of(context)
                        .extension<StackColors>()!
                        .tokenSummaryTextSecondary,
                    width: 12,
                    height: 12,
                  ),
                  const SizedBox(
                    width: 6,
                  ),
                  Text(
                    ref.watch(
                      pWalletName(walletId),
                    ),
                    style: STextStyles.w500_12(context).copyWith(
                      color: Theme.of(context)
                          .extension<StackColors>()!
                          .tokenSummaryTextSecondary,
                    ),
                  ),
                ],
              ),
              const SizedBox(
                height: 6,
              ),
              Row(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Text(
                    ref
                        .watch(
                          pAmountFormatter(
                            Ethereum(CryptoCurrencyNetwork.main),
                          ),
                        )
                        .format(
                          balance.total,
                          ethContract: token,
                        ),
                    style: STextStyles.pageTitleH1(context).copyWith(
                      color: Theme.of(context)
                          .extension<StackColors>()!
                          .tokenSummaryTextPrimary,
                    ),
                  ),
                  const SizedBox(
                    width: 10,
                  ),
                  CoinTickerTag(
                    walletId: walletId,
                  ),
                ],
              ),
              const SizedBox(
                height: 6,
              ),
              Text(
                "${(balance.total.decimal * ref.watch(
                          priceAnd24hChangeNotifierProvider.select(
                            (value) => value.getTokenPrice(token.address).item1,
                          ),
                        )).toAmount(
                      fractionDigits: 2,
                    ).fiatString(
                      locale: ref.watch(
                        localeServiceChangeNotifierProvider.select(
                          (value) => value.locale,
                        ),
                      ),
                    )} ${ref.watch(
                  prefsChangeNotifierProvider.select(
                    (value) => value.currency,
                  ),
                )}",
                style: STextStyles.subtitle500(context).copyWith(
                  color: Theme.of(context)
                      .extension<StackColors>()!
                      .tokenSummaryTextPrimary,
                ),
              ),
              const SizedBox(
                height: 20,
              ),
              TokenWalletOptions(
                walletId: walletId,
                tokenContract: token,
              ),
            ],
          ),
        ),
        Positioned(
          top: 10,
          right: 10,
          child: WalletRefreshButton(
            walletId: walletId,
            initialSyncStatus: initialSyncStatus,
            tokenContractAddress: ref.watch(
              pCurrentTokenWallet.select(
                (value) => value!.tokenContract.address,
              ),
            ),
            overrideIconColor:
                Theme.of(context).extension<StackColors>()!.topNavIconPrimary,
          ),
        ),
      ],
    );
  }
}

class TokenWalletOptions extends ConsumerWidget {
  const TokenWalletOptions({
    super.key,
    required this.walletId,
    required this.tokenContract,
  });

  final String walletId;
  final EthContract tokenContract;
  CryptoCurrency get ethereum => Ethereum(CryptoCurrencyNetwork.main);

  void _onExchangePressed(BuildContext context) async {
    unawaited(
      Navigator.of(context).pushNamed(
        WalletInitiatedExchangeView.routeName,
        arguments: Tuple3(
          walletId,
          ethereum,
          tokenContract,
        ),
      ),
    );
  }

  void _onBuyPressed(BuildContext context) {
    unawaited(
      Navigator.of(context).pushNamed(
        BuyInWalletView.routeName,
        arguments: Tuple2(
          ethereum,
          tokenContract,
        ),
      ),
    );
  }

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final prefs = ref.watch(prefsChangeNotifierProvider);
    final showExchange = prefs.enableExchange;

    return Row(
      mainAxisAlignment: MainAxisAlignment.center,
      children: [
        TokenOptionsButton(
          onPressed: () {
            Navigator.of(context).pushNamed(
              ReceiveView.routeName,
              arguments: Tuple2(
                walletId,
                tokenContract,
              ),
            );
          },
          subLabel: "Receive",
          iconAssetPathSVG: Assets.svg.arrowDownLeft,
        ),
        const SizedBox(
          width: 16,
        ),
        TokenOptionsButton(
          onPressed: () {
            Navigator.of(context).pushNamed(
              TokenSendView.routeName,
              arguments: Tuple3(
                walletId,
                ethereum,
                tokenContract,
              ),
            );
          },
          subLabel: "Send",
          iconAssetPathSVG: Assets.svg.arrowUpRight,
        ),
        if (AppConfig.hasFeature(AppFeature.swap) && showExchange)
          const SizedBox(
            width: 16,
          ),
        if (AppConfig.hasFeature(AppFeature.swap) && showExchange)
          TokenOptionsButton(
            onPressed: () => _onExchangePressed(context),
            subLabel: "Swap",
            iconAssetPathSVG: ref.watch(
              themeProvider.select(
                (value) => value.assets.exchange,
              ),
            ),
          ),
        if (AppConfig.hasFeature(AppFeature.buy) && showExchange)
          const SizedBox(
            width: 16,
          ),
        if (AppConfig.hasFeature(AppFeature.buy) && showExchange)
          TokenOptionsButton(
            onPressed: () => _onBuyPressed(context),
            subLabel: "Buy",
            iconAssetPathSVG: Assets.svg.creditCard,
          ),
      ],
    );
  }
}

class TokenOptionsButton extends StatelessWidget {
  const TokenOptionsButton({
    super.key,
    required this.onPressed,
    required this.subLabel,
    required this.iconAssetPathSVG,
  });

  final VoidCallback onPressed;
  final String subLabel;
  final String iconAssetPathSVG;

  @override
  Widget build(BuildContext context) {
    final iconSize = subLabel == "Send" || subLabel == "Receive" ? 12.0 : 24.0;
    return Column(
      mainAxisSize: MainAxisSize.min,
      crossAxisAlignment: CrossAxisAlignment.center,
      children: [
        RawMaterialButton(
          fillColor:
              Theme.of(context).extension<StackColors>()!.tokenSummaryButtonBG,
          elevation: 0,
          focusElevation: 0,
          hoverElevation: 0,
          highlightElevation: 0,
          constraints: const BoxConstraints(),
          shape: RoundedRectangleBorder(
            borderRadius: BorderRadius.circular(
              Constants.size.circularBorderRadius,
            ),
          ),
          onPressed: onPressed,
          child: Padding(
            padding: const EdgeInsets.all(10),
            child: ConditionalParent(
              condition: iconSize < 24,
              builder: (child) => RoundedContainer(
                padding: const EdgeInsets.all(6),
                color: Theme.of(context)
                    .extension<StackColors>()!
                    .tokenSummaryIcon
                    .withOpacity(0.4),
                radiusMultiplier: 10,
                child: Center(
                  child: child,
                ),
              ),
              child: iconAssetPathSVG.startsWith("assets/")
                  ? SvgPicture.asset(
                      iconAssetPathSVG,
                      color: Theme.of(context)
                          .extension<StackColors>()!
                          .tokenSummaryIcon,
                      width: iconSize,
                      height: iconSize,
                    )
                  : SvgPicture.file(
                      File(iconAssetPathSVG),
                      color: Theme.of(context)
                          .extension<StackColors>()!
                          .tokenSummaryIcon,
                      width: iconSize,
                      height: iconSize,
                    ),
            ),
          ),
        ),
        const SizedBox(
          height: 6,
        ),
        Text(
          subLabel,
          style: STextStyles.w500_12(context).copyWith(
            color: Theme.of(context)
                .extension<StackColors>()!
                .tokenSummaryTextPrimary,
          ),
        ),
      ],
    );
  }
}

