                    padding: const EdgeInsets.all(0),
                    child: ListView.separated(
                      shrinkWrap: true,
                      itemBuilder: (context, index) {
                        BorderRadius? radius;
                        if (widget.transactions.length == 1) {
                          radius = BorderRadius.circular(
