                ),
                child: SizedBox(
                  width: MediaQuery.of(context).size.width - 32,
                  child: TextButton(
                    style: ButtonStyle(
                      backgroundColor: MaterialStateProperty.all<Color>(
                        Theme.of(context).extension<StackColors>()!.textError,
                      ),
                    ),
                    onPressed: () async {
                      final wallet = ref.read(pWallets).getWallet(walletId);

                      if (wallet is EpiccashWallet) {
                        final String? id = _transaction.slateId;
                        if (id == null) {
                          unawaited(
                            showFloatingFlushBar(
                              type: FlushBarType.warning,
                              message: "Could not find Epic transaction ID",
                              context: context,
                            ),
                          );
                          return;
                        }

                        unawaited(
                          showDialog<void>(
                            barrierDismissible: false,
                            context: context,
                            builder: (_) =>
                                const CancellingTransactionProgressDialog(),
                          ),
                        );

                        final result =
                            await wallet.cancelPendingTransactionAndPost(id);
                        if (mounted) {
                          // pop progress dialog
                          Navigator.of(context).pop();

                          if (result.isEmpty) {
                            await showDialog<dynamic>(
                              context: context,
                              builder: (_) => StackOkDialog(
                                title: "Transaction cancelled",
                                onOkPressed: (_) {
                                  wallet.refresh();
                                  Navigator.of(context).popUntil(
                                    ModalRoute.withName(
                                      WalletView.routeName,
                                    ),
                                  );
                                },
                              ),
                            );
                          } else {
                            await showDialog<dynamic>(
                              context: context,
                              builder: (_) => StackOkDialog(
                                title: "Failed to cancel transaction",
                                message: result,
                              ),
                            );
                          }
                        }
                      } else {
                        unawaited(
                          showFloatingFlushBar(
                            type: FlushBarType.warning,
                            message: "ERROR: Wallet type is not Epic Cash",
                            context: context,
                          ),
                        );
                        return;
                      }
                    },
                    child: Text(
                      "Cancel Transaction",
                      style: STextStyles.button(context),
                    ),
                  ),
                ),
              )
            : null,
      ),
    );
  }
}

class OutputCard extends ConsumerWidget {
