class CoinWalletsTable extends ConsumerWidget {
  const CoinWalletsTable({
    super.key,
    required this.coin,
  });

  final CryptoCurrency coin;

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final walletIds = ref
        .watch(pWallets)
        .wallets
        .where((e) => e.info.coin == coin)
        .map((e) => e.walletId)
        .toList();

    return Container(
      decoration: BoxDecoration(
        color: Theme.of(context).extension<StackColors>()!.popupBG,
        borderRadius: BorderRadius.circular(
          Constants.size.circularBorderRadius,
        ),
      ),
      child: Padding(
        padding: const EdgeInsets.symmetric(
          // horizontal: 20,
          // vertical: 16,
          horizontal: 6,
          vertical: 6,
        ),
        child: Column(
          children: [
            for (int i = 0; i < walletIds.length; i++)
              Column(
                key: Key("${coin.identifier}_$runtimeType${walletIds[i]}_key"),
                children: [
                  if (i != 0)
                    const SizedBox(
                      height: 8,
                    ),
                  Stack(
                    children: [
                      WalletInfoRow(
                        padding: const EdgeInsets.symmetric(
                          horizontal: 14,
                          vertical: 10,
                        ),
                        walletId: walletIds[i],
                      ),
                      Positioned.fill(
                        child: WalletRowHoverOverlay(
                          onPressed: () async {
                            ref.read(currentWalletIdProvider.state).state =
                                walletIds[i];

                            final wallet =
                                ref.read(pWallets).getWallet(walletIds[i]);

                            final canContinue =
                                await checkShowNodeTorSettingsMismatch(
                              context: context,
                              currency: coin,
                              prefs: ref.read(prefsChangeNotifierProvider),
                              nodeService:
                                  ref.read(nodeServiceChangeNotifierProvider),
                              allowCancel: true,
                              rootNavigator: Util.isDesktop,
                            );

                            if (!canContinue) {
                              return;
                            }

                            final Future<void> loadFuture;
                            if (wallet is ExternalWallet) {
                              loadFuture = wallet
                                  .init()
                                  .then((value) async => await (wallet).open());
                            } else {
                              loadFuture = wallet.init();
                            }
                            await showLoading(
                              whileFuture: loadFuture,
                              context: context,
                              message: 'Opening ${wallet.info.name}',
                              rootNavigator: Util.isDesktop,
                            );

                            if (context.mounted) {
                              await Navigator.of(context).pushNamed(
                                DesktopWalletView.routeName,
                                arguments: walletIds[i],
                              );
                            }
                          },
                        ),
                      ),
                    ],
                  ),
                ],
              ),
          ],
        ),
      ),
    );
  }
}

class WalletRowHoverOverlay extends StatefulWidget {
  const WalletRowHoverOverlay({
    super.key,
    required this.onPressed,
  });

  final VoidCallback onPressed;

  @override
  State<WalletRowHoverOverlay> createState() => _WalletRowHoverOverlayState();
}

class _WalletRowHoverOverlayState extends State<WalletRowHoverOverlay> {
  late final VoidCallback onPressed;

  bool _hovering = false;

  @override
  void initState() {
    onPressed = widget.onPressed;
    super.initState();
  }

  @override
  Widget build(BuildContext context) {
    return MouseRegion(
      cursor: SystemMouseCursors.click,
      onEnter: (_) {
        setState(() {
          _hovering = true;
        });
      },
      onExit: (_) {
        setState(() {
          _hovering = false;
        });
      },
      child: GestureDetector(
        onTap: onPressed,
        child: AnimatedOpacity(
          duration: const Duration(milliseconds: 100),
          opacity: _hovering ? 0.1 : 0,
          child: RoundedContainer(
            color:
                Theme.of(context).extension<StackColors>()!.buttonBackSecondary,
          ),
        ),
      ),
    );
  }
}
