  Future<void> previewSend() async {
    final wallet = ref.read(pWallets).getWallet(walletId);

    final Amount amount = ref.read(pSendAmount)!;
    final Amount availableBalance;
    if ((coin == Coin.firo || coin == Coin.firoTestNet)) {
      switch (ref.read(publicPrivateBalanceStateProvider.state).state) {
        case FiroType.public:
          availableBalance = wallet.info.cachedBalance.spendable;
          break;
        case FiroType.lelantus:
          availableBalance = wallet.info.cachedBalanceSecondary.spendable;
          break;
        case FiroType.spark:
          availableBalance = wallet.info.cachedBalanceTertiary.spendable;
          break;
      }
    } else {
      availableBalance = wallet.info.cachedBalance.spendable;
    }

    final coinControlEnabled =
        ref.read(prefsChangeNotifierProvider).enableCoinControl;

    if (!(wallet is CoinControlInterface && coinControlEnabled) ||
        (coinControlEnabled &&
            ref.read(desktopUseUTXOs).isEmpty)) {
      // confirm send all
      if (amount == availableBalance) {
        final bool? shouldSendAll = await showDialog<bool>(
          context: context,
          useSafeArea: false,
          barrierDismissible: true,
          builder: (context) {
            return DesktopDialog(
              maxWidth: 450,
              maxHeight: double.infinity,
              child: Padding(
                padding: const EdgeInsets.only(
                  left: 32,
                  bottom: 32,
                ),
                child: Column(
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: [
                    Row(
                      mainAxisAlignment: MainAxisAlignment.spaceBetween,
                      children: [
                        Text(
                          "Confirm send all",
                          style: STextStyles.desktopH3(context),
                        ),
                        const DesktopDialogCloseButton(),
                      ],
                    ),
                    const SizedBox(
                      height: 12,
                    ),
                    Padding(
                      padding: const EdgeInsets.only(
                        right: 32,
                      ),
                      child: Text(
                        "You are about to send your entire balance. Would you like to continue?",
                        textAlign: TextAlign.left,
                        style: STextStyles.desktopTextExtraExtraSmall(context)
                            .copyWith(
                          fontSize: 18,
                        ),
                      ),
                    ),
                    const SizedBox(
                      height: 40,
                    ),
                    Padding(
                      padding: const EdgeInsets.only(
                        right: 32,
                      ),
                      child: Row(
                        children: [
                          Expanded(
                            child: SecondaryButton(
                              buttonHeight: ButtonHeight.l,
                              label: "Cancel",
                              onPressed: () {
                                Navigator.of(context).pop(false);
                              },
                            ),
                          ),
                          const SizedBox(
                            width: 16,
                          ),
                          Expanded(
                            child: PrimaryButton(
                              buttonHeight: ButtonHeight.l,
                              label: "Yes",
                              onPressed: () {
                                Navigator.of(context).pop(true);
                              },
                            ),
                          ),
                        ],
                      ),
                    ),
                  ],
                ),
              ),
            );
          },
        );

        if (shouldSendAll == null || shouldSendAll == false) {
          // cancel preview
          return;
        }
      }
    }

    try {
      bool wasCancelled = false;

      if (mounted) {
        unawaited(
          showDialog<dynamic>(
            context: context,
            useSafeArea: false,
            barrierDismissible: false,
            builder: (context) {
              return DesktopDialog(
                maxWidth: 400,
                maxHeight: double.infinity,
                child: Padding(
                  padding: const EdgeInsets.all(32),
                  child: BuildingTransactionDialog(
                    coin: wallet.info.coin,
                    onCancel: () {
                      wasCancelled = true;

                      Navigator.of(context).pop();
                    },
                  ),
                ),
              );
            },
          ),
        );
      }

      final time = Future<dynamic>.delayed(
        const Duration(
          milliseconds: 2500,
        ),
      );

      TxData txData;
      Future<TxData> txDataFuture;

      if (isPaynymSend) {
        final paynymWallet = wallet as PaynymInterface;

        final feeRate = ref.read(feeRateTypeStateProvider);
        txDataFuture = paynymWallet.preparePaymentCodeSend(
          txData: TxData(
            paynymAccountLite: widget.accountLite!,
            recipients: [
              (
                address: widget.accountLite!.code,
                amount: amount,
                isChange: false,
              )
            ],
            satsPerVByte: isCustomFee ? customFeeRate : null,
            feeRateType: feeRate,
            utxos: (wallet is CoinControlInterface &&
                    coinControlEnabled &&
                    ref.read(desktopUseUTXOs).isNotEmpty)
                ? ref.read(desktopUseUTXOs)
                : null,
          ),
        );
      } else if (wallet is FiroWallet) {
        switch (ref.read(publicPrivateBalanceStateProvider.state).state) {
          case FiroType.public:
            if (ref.read(pValidSparkSendToAddress)) {
              txDataFuture = wallet.prepareSparkMintTransaction(
                txData: TxData(
                  sparkRecipients: [
                    (
                      address: _address!,
                      amount: amount,
                      memo: memoController.text,
                      isChange: false,
                    )
                  ],
                  feeRateType: ref.read(feeRateTypeStateProvider),
                  satsPerVByte: isCustomFee ? customFeeRate : null,
                  utxos: (wallet is CoinControlInterface &&
                          coinControlEnabled &&
                          ref.read(desktopUseUTXOs).isNotEmpty)
                      ? ref.read(desktopUseUTXOs)
                      : null,
                ),
              );
            } else {
              txDataFuture = wallet.prepareSend(
                txData: TxData(
                  recipients: [
                    (
                      address: _address!,
                      amount: amount,
                      isChange: false,
                    )
                  ],
                  feeRateType: ref.read(feeRateTypeStateProvider),
                  satsPerVByte: isCustomFee ? customFeeRate : null,
                  utxos: (wallet is CoinControlInterface &&
                          coinControlEnabled &&
                          ref.read(desktopUseUTXOs).isNotEmpty)
                      ? ref.read(desktopUseUTXOs)
                      : null,
                ),
              );
            }
            break;

          case FiroType.lelantus:
            txDataFuture = wallet.prepareSendLelantus(
              txData: TxData(
                recipients: [
                  (
                    address: _address!,
                    amount: amount,
                    isChange: false,
                  )
                ],
              ),
            );
            break;

          case FiroType.spark:
            txDataFuture = wallet.prepareSendSpark(
              txData: TxData(
                recipients: ref.read(pValidSparkSendToAddress)
                    ? null
                    : [
                        (
                          address: _address!,
                          amount: amount,
                          isChange: false,
                        )
                      ],
                sparkRecipients: ref.read(pValidSparkSendToAddress)
                    ? [
                        (
                          address: _address!,
                          amount: amount,
                          memo: memoController.text,
                          isChange: false,
                        )
                      ]
                    : null,
              ),
            );
            break;
        }
      } else {
        final memo = isStellar ? memoController.text : null;
        txDataFuture = wallet.prepareSend(
          txData: TxData(
            recipients: [
              (
                address: _address!,
                amount: amount,
                isChange: false,
              )
            ],
            memo: memo,
            feeRateType: ref.read(feeRateTypeStateProvider),
            satsPerVByte: isCustomFee ? customFeeRate : null,
            utxos: (wallet is CoinControlInterface &&
                    coinControlEnabled &&
                    ref.read(desktopUseUTXOs).isNotEmpty)
                ? ref.read(desktopUseUTXOs)
                : null,
          ),
        );
      }

      final results = await Future.wait([
        txDataFuture,
        time,
      ]);

      txData = results.first as TxData;

      if (!wasCancelled && mounted) {
        if (isPaynymSend) {
          txData = txData.copyWith(
            paynymAccountLite: widget.accountLite!,
            note: _note ?? "PayNym send",
          );
        } else {
          txData = txData.copyWith(
            note: _note ?? "",
          );
          if (coin == Coin.epicCash) {
            txData = txData.copyWith(
              noteOnChain: _onChainNote ?? "",
            );
          }
        }
        // pop building dialog
        Navigator.of(
          context,
          rootNavigator: true,
        ).pop();

        unawaited(
          showDialog(
            context: context,
            builder: (context) => DesktopDialog(
              maxHeight: MediaQuery.of(context).size.height - 64,
              maxWidth: 580,
              child: ConfirmTransactionView(
                txData: txData,
                walletId: walletId,
                onSuccess: clearSendForm,
                isPaynymTransaction: isPaynymSend,
                routeOnSuccessName: DesktopHomeView.routeName,
              ),
            ),
          ),
        );
      }
    } catch (e, s) {
      Logging.instance.log("Desktop send: $e\n$s", level: LogLevel.Warning);
      if (mounted) {
        // pop building dialog
        Navigator.of(
          context,
          rootNavigator: true,
        ).pop();

        unawaited(
          showDialog<void>(
            context: context,
            builder: (context) {
              return DesktopDialog(
                maxWidth: 450,
                maxHeight: double.infinity,
                child: Padding(
                  padding: const EdgeInsets.only(
                    left: 32,
                    bottom: 32,
                  ),
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      Row(
                        mainAxisAlignment: MainAxisAlignment.spaceBetween,
                        children: [
                          Text(
                            "Transaction failed",
                            style: STextStyles.desktopH3(context),
                          ),
                          const DesktopDialogCloseButton(),
                        ],
                      ),
                      const SizedBox(
                        height: 12,
                      ),
                      Padding(
                        padding: const EdgeInsets.only(
                          right: 32,
                        ),
                        child: Text(
                          e.toString(),
                          textAlign: TextAlign.left,
                          style: STextStyles.desktopTextExtraExtraSmall(context)
                              .copyWith(
                            fontSize: 18,
                          ),
                        ),
                      ),
                      const SizedBox(
                        height: 40,
                      ),
                      Row(
                        children: [
                          Expanded(
                            child: SecondaryButton(
                              buttonHeight: ButtonHeight.l,
                              label: "Ok",
                              onPressed: () {
                                Navigator.of(
                                  context,
                                  rootNavigator: true,
                                ).pop();
                              },
                            ),
                          ),
                          const SizedBox(
                            width: 32,
                          ),
                        ],
                      ),
                    ],
                  ),
                ),
              );
            },
          ),
        );
      }
    }
  }

  void clearSendForm() {
