  Future<void> scanWebcam() async {
    try {
      final qrResult = await showDialog<String>(
        context: context,
        builder: (context) => const QrCodeScannerDialog(),
      );
      if (qrResult == null) {
        Logging.instance.w("Qr scanning cancelled");
      } else {
        try {
          _processQrCodeData(qrResult);
        } catch (e, s) {
          Logging.instance.e(
            "Error processing QR code data",
            error: e,
            stackTrace: s,
          );
        }
      }
    } catch (e, s) {
      Logging.instance.e(
        "Error opening QR code scanner dialog",
        error: e,
        stackTrace: s,
      );
    }
  }

  Future<void> previewSend() async {
    final wallet = ref.read(pWallets).getWallet(walletId);

    final Amount amount = ref.read(pSendAmount)!;
    final Amount availableBalance;
    if ((coin is Firo)) {
      switch (ref.read(publicPrivateBalanceStateProvider.state).state) {
        case BalanceType.public:
          availableBalance = wallet.info.cachedBalance.spendable;
          break;
        case BalanceType.private:
          availableBalance =
              coin is Firo
                  ? wallet.info.cachedBalanceTertiary.spendable
                  : wallet.info.cachedBalanceSecondary.spendable;
          break;
      }
    } else {
      availableBalance = wallet.info.cachedBalance.spendable;
    }

    final coinControlEnabled =
        ref.read(prefsChangeNotifierProvider).enableCoinControl;

    if (!(wallet is CoinControlInterface && coinControlEnabled) ||
        (coinControlEnabled && ref.read(desktopUseUTXOs).isEmpty)) {
      // confirm send all
      if (amount == availableBalance) {
        final bool? shouldSendAll = await showDialog<bool>(
          context: context,
          useSafeArea: false,
          barrierDismissible: true,
          builder: (context) {
            return DesktopDialog(
              maxWidth: 450,
              maxHeight: double.infinity,
              child: Padding(
                padding: const EdgeInsets.only(left: 32, bottom: 32),
                child: Column(
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: [
                    Row(
                      mainAxisAlignment: MainAxisAlignment.spaceBetween,
                      children: [
                        Text(
                          "Confirm send all",
                          style: STextStyles.desktopH3(context),
                        ),
                        const DesktopDialogCloseButton(),
                      ],
                    ),
                    const SizedBox(height: 12),
                    Padding(
                      padding: const EdgeInsets.only(right: 32),
                      child: Text(
                        "You are about to send your entire balance. Would you like to continue?",
                        textAlign: TextAlign.left,
                        style: STextStyles.desktopTextExtraExtraSmall(
                          context,
                        ).copyWith(fontSize: 18),
                      ),
                    ),
                    const SizedBox(height: 40),
                    Padding(
                      padding: const EdgeInsets.only(right: 32),
                      child: Row(
                        children: [
                          Expanded(
                            child: SecondaryButton(
                              buttonHeight: ButtonHeight.l,
                              label: "Cancel",
                              onPressed: () {
                                Navigator.of(context).pop(false);
                              },
                            ),
                          ),
                          const SizedBox(width: 16),
                          Expanded(
                            child: PrimaryButton(
                              buttonHeight: ButtonHeight.l,
                              label: "Yes",
                              onPressed: () {
                                Navigator.of(context).pop(true);
                              },
                            ),
                          ),
                        ],
                      ),
                    ),
                  ],
                ),
              ),
            );
          },
        );

        if (shouldSendAll == null || shouldSendAll == false) {
          // cancel preview
          return;
        }
      }
    }

    try {
      bool wasCancelled = false;

      if (mounted) {
        unawaited(
          showDialog<dynamic>(
            context: context,
            useSafeArea: false,
            barrierDismissible: false,
            builder: (context) {
              return DesktopDialog(
                maxWidth: 400,
                maxHeight: double.infinity,
                child: Padding(
                  padding: const EdgeInsets.all(32),
                  child: BuildingTransactionDialog(
                    coin: wallet.info.coin,
                    isSpark:
                        wallet is FiroWallet &&
                        ref
                                .read(publicPrivateBalanceStateProvider.state)
                                .state ==
                            BalanceType.private,
                    onCancel: () {
                      wasCancelled = true;

                      Navigator.of(context).pop();
                    },
                  ),
                ),
              );
            },
          ),
        );
      }

      final time = Future<dynamic>.delayed(const Duration(milliseconds: 2500));

      TxData txData;
      Future<TxData> txDataFuture;

      if (isPaynymSend) {
        final paynymWallet = wallet as PaynymInterface;

        final feeRate = ref.read(feeRateTypeDesktopStateProvider);
        txDataFuture = paynymWallet.preparePaymentCodeSend(
          txData: TxData(
            paynymAccountLite: widget.accountLite!,
            recipients: [
              (
                address: widget.accountLite!.code,
                amount: amount,
                isChange: false,
              ),
            ],
            satsPerVByte: isCustomFee ? customFeeRate : null,
            feeRateType: feeRate,
            utxos:
                (wallet is CoinControlInterface &&
                        coinControlEnabled &&
                        ref.read(desktopUseUTXOs).isNotEmpty)
                    ? ref.read(desktopUseUTXOs)
                    : null,
          ),
        );
      } else if (wallet is FiroWallet) {
        switch (ref.read(publicPrivateBalanceStateProvider.state).state) {
          case BalanceType.public:
            if (ref.read(pValidSparkSendToAddress)) {
              txDataFuture = wallet.prepareSparkMintTransaction(
                txData: TxData(
                  sparkRecipients: [
                    (
                      address: _address!,
                      amount: amount,
                      memo: memoController.text,
                      isChange: false,
                    ),
                  ],
                  feeRateType: ref.read(feeRateTypeDesktopStateProvider),
                  satsPerVByte: isCustomFee ? customFeeRate : null,
                  utxos:
                      (coinControlEnabled &&
                              ref.read(desktopUseUTXOs).isNotEmpty)
                          ? ref.read(desktopUseUTXOs)
                          : null,
                ),
              );
            } else {
              txDataFuture = wallet.prepareSend(
                txData: TxData(
                  recipients: [
                    (address: _address!, amount: amount, isChange: false),
                  ],
                  feeRateType: ref.read(feeRateTypeDesktopStateProvider),
                  satsPerVByte: isCustomFee ? customFeeRate : null,
                  utxos:
                      (coinControlEnabled &&
                              ref.read(desktopUseUTXOs).isNotEmpty)
                          ? ref.read(desktopUseUTXOs)
                          : null,
                ),
              );
            }
            break;

          case BalanceType.private:
            txDataFuture = wallet.prepareSendSpark(
              txData: TxData(
                recipients:
                    ref.read(pValidSparkSendToAddress)
                        ? null
                        : [
                          (address: _address!, amount: amount, isChange: false),
                        ],
                sparkRecipients:
                    ref.read(pValidSparkSendToAddress)
                        ? [
                          (
                            address: _address!,
                            amount: amount,
                            memo: memoController.text,
                            isChange: false,
                          ),
                        ]
                        : null,
              ),
            );
            break;
        }
      } else {
        final memo = isStellar ? memoController.text : null;
        txDataFuture = wallet.prepareSend(
          txData: TxData(
            recipients: [(address: _address!, amount: amount, isChange: false)],
            memo: memo,
            feeRateType: ref.read(feeRateTypeDesktopStateProvider),
            satsPerVByte: isCustomFee ? customFeeRate : null,
            nonce:
                wallet.cryptoCurrency is Ethereum
                    ? int.tryParse(nonceController.text)
                    : null,
            utxos:
                (wallet is CoinControlInterface &&
                        coinControlEnabled &&
                        ref.read(desktopUseUTXOs).isNotEmpty)
                    ? ref.read(desktopUseUTXOs)
                    : null,
            ethEIP1559Fee: ethFee,
          ),
        );
      }

      final results = await Future.wait([txDataFuture, time]);

      txData = results.first as TxData;

      if (!wasCancelled && mounted) {
        if (isPaynymSend) {
          txData = txData.copyWith(
            paynymAccountLite: widget.accountLite!,
            note: _note ?? "PayNym send",
          );
        } else {
          txData = txData.copyWith(note: _note ?? "");
          if (coin is Epiccash) {
            txData = txData.copyWith(noteOnChain: _onChainNote ?? "");
          }
        }
        // pop building dialog
        Navigator.of(context, rootNavigator: true).pop();

        unawaited(
          showDialog(
            context: context,
            builder:
                (context) => DesktopDialog(
                  maxHeight: MediaQuery.of(context).size.height - 64,
                  maxWidth: 580,
                  child: ConfirmTransactionView(
                    txData: txData,
                    walletId: walletId,
                    onSuccess: clearSendForm,
                    isPaynymTransaction: isPaynymSend,
                    routeOnSuccessName: DesktopHomeView.routeName,
                  ),
                ),
          ),
        );
      }
    } catch (e, s) {
      Logging.instance.e("Desktop send: ", error: e, stackTrace: s);
      if (mounted) {
        // pop building dialog
        Navigator.of(context, rootNavigator: true).pop();

        unawaited(
          showDialog<void>(
            context: context,
            builder: (context) {
              return DesktopDialog(
                maxWidth: 450,
                maxHeight: double.infinity,
                child: Padding(
                  padding: const EdgeInsets.only(left: 32, bottom: 32),
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      Row(
                        mainAxisAlignment: MainAxisAlignment.spaceBetween,
                        children: [
                          Text(
                            "Transaction failed",
                            style: STextStyles.desktopH3(context),
                          ),
                          const DesktopDialogCloseButton(),
                        ],
                      ),
                      const SizedBox(height: 12),
                      Padding(
                        padding: const EdgeInsets.only(right: 32),
                        child: Text(
                          e.toString(),
                          textAlign: TextAlign.left,
                          style: STextStyles.desktopTextExtraExtraSmall(
                            context,
                          ).copyWith(fontSize: 18),
                        ),
                      ),
                      const SizedBox(height: 40),
                      Row(
                        children: [
                          Expanded(
                            child: SecondaryButton(
                              buttonHeight: ButtonHeight.l,
                              label: "Ok",
                              onPressed: () {
                                Navigator.of(
                                  context,
                                  rootNavigator: true,
                                ).pop();
                              },
                            ),
                          ),
                          const SizedBox(width: 32),
                        ],
                      ),
                    ],
                  ),
                ),
              );
            },
          ),
        );
      }
    }
  }

  void clearSendForm() {
    sendToController.text = "";
    cryptoAmountController.text = "";
    baseAmountController.text = "";
    memoController.text = "";
    nonceController.text = "";
    _address = "";
    _addressToggleFlag = false;
    if (mounted) {
      setState(() {});
    }
  }

  void _cryptoAmountChanged() async {
    if (!_cryptoAmountChangeLock) {
      final cryptoAmount = ref
          .read(pAmountFormatter(coin))
          .tryParse(cryptoAmountController.text);
      final Amount? amount;
      if (cryptoAmount != null) {
        amount = cryptoAmount;
        if (_cachedAmountToSend != null && _cachedAmountToSend == amount) {
          return;
        }

        _cachedAmountToSend = amount;

        final price =
            ref.read(priceAnd24hChangeNotifierProvider).getPrice(coin)?.value;

        if (price != null && price > Decimal.zero) {
          final String fiatAmountString = (amount.decimal * price)
              .toAmount(fractionDigits: 2)
              .fiatString(
                locale: ref.read(localeServiceChangeNotifierProvider).locale,
              );

          baseAmountController.text = fiatAmountString;
        }
      } else {
        amount = null;
        _cachedAmountToSend = null;
        baseAmountController.text = "";
      }

      ref.read(pSendAmount.notifier).state = amount;
    }
  }

