  void _processQrCodeData(String qrCodeData) {
    try {
      final paymentData = AddressUtils.parsePaymentUri(
        qrCodeData,
        logging: Logging.instance,
      );

      if (paymentData != null &&
          paymentData.coin?.uriScheme == coin.uriScheme) {
        _applyUri(paymentData);
      } else {
        _address = qrCodeData.split("\n").first.trim();
        sendToController.text = _address ?? "";

        _setValidAddressProviders(_address);
        setState(() {
          _addressToggleFlag = sendToController.text.isNotEmpty;
        });
      }
    } catch (e, s) {
      Logging.instance.e(
        "Error processing QR code data",
        error: e,
        stackTrace: s,
      );
    }
  }

  void _setValidAddressProviders(String? address) {
    if (isPaynymSend) {
      ref.read(pValidSendToAddress.notifier).state = true;
    } else {
      final wallet = ref.read(pWallets).getWallet(walletId);
      if (wallet is SparkInterface) {
        ref
            .read(pValidSparkSendToAddress.notifier)
            .state = SparkInterface.validateSparkAddress(
          address: address ?? "",
          isTestNet: wallet.cryptoCurrency.network.isTestNet,
        );

        ref.read(pIsExchangeAddress.state).state = (coin as Firo)
            .isExchangeAddress(address ?? "");

        if (ref.read(publicPrivateBalanceStateProvider) ==
                BalanceType.private &&
            ref.read(pIsExchangeAddress) &&
            !_isFiroExWarningDisplayed) {
          _isFiroExWarningDisplayed = true;
          showFiroExchangeAddressWarning(
            context,
            () => _isFiroExWarningDisplayed = false,
          );
        }
      }

      ref.read(pValidSendToAddress.notifier).state = wallet.cryptoCurrency
          .validateAddress(address ?? "");
    }
  }

  void _applyUri(PaymentUriData paymentData) {
    try {
      // auto fill address
      _address = paymentData.address;
      sendToController.text = _address!;

      // autofill notes field.
      if (paymentData.message != null) {
        _note = paymentData.message;
      } else if (paymentData.label != null) {
        _note = paymentData.label;
      }

      // autofill amount field
      if (paymentData.amount != null) {
        final amount = Decimal.parse(
          paymentData.amount!,
        ).toAmount(fractionDigits: coin.fractionDigits);
        cryptoAmountController.text = ref
            .read(pAmountFormatter(coin))
            .format(amount, withUnitName: false);
        ref.read(pSendAmount.notifier).state = amount;
      }

      // Trigger validation after pasting.
      _setValidAddressProviders(_address);
      setState(() {
        _addressToggleFlag = sendToController.text.isNotEmpty;
      });
    } catch (e, s) {
      Logging.instance.e("Error applying URI", error: e, stackTrace: s);
    }
  }

  Future<void> _checkSparkNameAndOrSetAddress(
    String content, {
    bool setController = true,
  }) async {
    void setContent() {
      if (setController) {
        sendToController.text = content;
      }
      _address = content;
    }

    // check for spark name
    if (coin is Firo) {
      final address = await SparkNamesService.getAddressFor(
        content,
        network: coin.network,
      );
      if (address != null) {
        // found a spark name
        sendToController.text = content;
        _address = address;
      } else {
        setContent();
      }
    } else {
      setContent();
    }
  }

  Future<void> pasteAddress() async {
    final ClipboardData? data = await clipboard.getData(Clipboard.kTextPlain);
    if (data?.text != null && data!.text!.isNotEmpty) {
      String content = data.text!.trim();
      if (content.contains("\n")) {
        content = content.substring(0, content.indexOf("\n")).trim();
      }

      try {
        final paymentData = AddressUtils.parsePaymentUri(
          content,
          logging: Logging.instance,
        );
        if (paymentData != null &&
            paymentData.coin?.uriScheme == coin.uriScheme) {
          _applyUri(paymentData);
        } else {
          if (coin is Epiccash) {
            content = AddressUtils().formatEpicCashAddress(content);
          }

          sendToController.text = content;
          _address = content;

          _setValidAddressProviders(_address);
          setState(() {
            _addressToggleFlag = sendToController.text.isNotEmpty;
          });
        }
      } catch (e) {
        // If parsing fails, treat it as a plain address.
        if (coin is Epiccash) {
          // strip http:// and https:// if content contains @
          content = AddressUtils().formatEpicCashAddress(content);
        }

        await _checkSparkNameAndOrSetAddress(content);

        // Trigger validation after pasting.
        _setValidAddressProviders(_address);
        setState(() {
          _addressToggleFlag = sendToController.text.isNotEmpty;
        });
      }
    }
  }

  Future<void> pasteMemo() async {
    if (memoController.text.isNotEmpty) {
      setState(() {
        memoController.text = "";
      });
    } else {
      final ClipboardData? data = await clipboard.getData(Clipboard.kTextPlain);
      if (data?.text != null && data!.text!.isNotEmpty) {
        final String content = data.text!.trim();

        setState(() {
          memoController.text = content;
        });
      }
    }
  }

  void fiatTextFieldOnChanged(String baseAmountString) {
    final baseAmount = Amount.tryParseFiatString(
      baseAmountString,
      locale: ref.read(localeServiceChangeNotifierProvider).locale,
    );
    final Amount? amount;
    if (baseAmount != null) {
      final _price =
          ref.read(priceAnd24hChangeNotifierProvider).getPrice(coin)?.value;

      if (_price == null || _price == Decimal.zero) {
        amount = Decimal.zero.toAmount(fractionDigits: coin.fractionDigits);
      } else {
        amount =
            baseAmount <= Amount.zero
                ? Decimal.zero.toAmount(fractionDigits: coin.fractionDigits)
                : (baseAmount.decimal / _price)
                    .toDecimal(scaleOnInfinitePrecision: coin.fractionDigits)
                    .toAmount(fractionDigits: coin.fractionDigits);
      }
      if (_cachedAmountToSend != null && _cachedAmountToSend == amount) {
        return;
      }
      _cachedAmountToSend = amount;

      final amountString = ref
          .read(pAmountFormatter(coin))
          .format(amount, withUnitName: false);

      _cryptoAmountChangeLock = true;
      cryptoAmountController.text = amountString;
      _cryptoAmountChangeLock = false;
    } else {
      amount = Decimal.zero.toAmount(fractionDigits: coin.fractionDigits);
      _cryptoAmountChangeLock = true;
      cryptoAmountController.text = "";
      _cryptoAmountChangeLock = false;
    }
    // setState(() {
    //   _calculateFeesFuture = calculateFees(
    //       Format.decimalAmountToSatoshis(
    //           _amountToSend!));
    // });
    ref.read(pSendAmount.notifier).state = amount;
  }

  String _getSendAllTitle(bool showCoinControl, Set<UTXO> selectedUTXOs) {
    if (showCoinControl && selectedUTXOs.isNotEmpty) {
      return "Send all selected";
    }

    return "Send all ${coin.ticker}";
  }

  Amount _selectedUtxosAmount(Set<UTXO> utxos) => Amount(
    rawValue: utxos.map((e) => BigInt.from(e.value)).reduce((v, e) => v += e),
    fractionDigits: ref.read(pWalletCoin(walletId)).fractionDigits,
  );

  Future<void> _sendAllTapped(bool showCoinControl) async {
    final Amount amount;

    if (showCoinControl && ref.read(desktopUseUTXOs).isNotEmpty) {
      amount = _selectedUtxosAmount(ref.read(desktopUseUTXOs));
    } else if (coin is Firo) {
      switch (ref.read(publicPrivateBalanceStateProvider.state).state) {
        case BalanceType.public:
          amount = ref.read(pWalletBalance(walletId)).spendable;
          break;
        case BalanceType.private:
          amount = ref.read(pWalletBalanceTertiary(walletId)).spendable;
          break;
      }
    } else {
      amount = ref.read(pWalletBalance(walletId)).spendable;
    }

    cryptoAmountController.text = ref
        .read(pAmountFormatter(coin))
        .format(amount, withUnitName: false);
  }

  void _showDesktopCoinControl() async {
    final amount = ref.read(pSendAmount);
    await showDialog<void>(
      context: context,
      builder:
          (context) => DesktopCoinControlUseDialog(
            walletId: widget.walletId,
            amountToSend: amount,
          ),
    );
  }

  @override
  void initState() {
    WidgetsBinding.instance.addPostFrameCallback((_) {
      ref.refresh(feeSheetSessionCacheProvider);
      ref.refresh(pIsExchangeAddress);
      ref.read(pValidSendToAddress.state).state = false;
      ref.read(pValidSparkSendToAddress.state).state = false;
    });

    // _calculateFeesFuture = calculateFees(0);
    _data = widget.autoFillData;
    walletId = widget.walletId;
    coin = ref.read(pWalletInfo(walletId)).coin;
    clipboard = widget.clipboard;

    isStellar = coin is Stellar;

    sendToController = TextEditingController();
    cryptoAmountController = TextEditingController();
    baseAmountController = TextEditingController();
    memoController = TextEditingController();
    nonceController = TextEditingController();

    onCryptoAmountChanged = _cryptoAmountChanged;
    cryptoAmountController.addListener(onCryptoAmountChanged);

    if (_data != null) {
      if (_data.amount != null) {
        cryptoAmountController.text = _data.amount!.toString();
      }
      sendToController.text = _data.contactLabel;
      _address = _data.address;
      _addressToggleFlag = true;
    }

    if (isPaynymSend) {
      sendToController.text = widget.accountLite!.nymName;
      WidgetsBinding.instance.addPostFrameCallback(
        (_) => _setValidAddressProviders(sendToController.text),
      );
    }

    _cryptoFocus.addListener(() {
      if (!_cryptoFocus.hasFocus && !_baseFocus.hasFocus) {
        if (ref.read(pSendAmount) == null) {
          if (ref.read(sendAmountProvider) != Amount.zero && mounted) {
            ref.read(sendAmountProvider.state).state = Amount.zero;
          }
        } else {
          ref.read(sendAmountProvider.state).state = ref.read(pSendAmount)!;
        }
      }
    });

    _baseFocus.addListener(() {
      if (!_cryptoFocus.hasFocus && !_baseFocus.hasFocus) {
        if (ref.read(pSendAmount) == null) {
          ref.refresh(sendAmountProvider);
        } else {
          ref.read(sendAmountProvider.state).state = ref.read(pSendAmount)!;
        }
      }
    });

    super.initState();
  }

  @override
  void dispose() {
    cryptoAmountController.removeListener(onCryptoAmountChanged);

    sendToController.dispose();
    cryptoAmountController.dispose();
    baseAmountController.dispose();
    memoController.dispose();
    nonceController.dispose();

    _addressFocusNode.dispose();
    _cryptoFocus.dispose();
    _baseFocus.dispose();
    _memoFocus.dispose();
    _nonceFocusNode.dispose();
    super.dispose();
  }

