Coin coinFromTickerCaseInsensitive(String ticker) {
  switch (ticker.toLowerCase()) {
    case "btc":
      return Coin.bitcoin;
    case "ltc":
      return Coin.litecoin;
    case "bch":
      return Coin.bitcoincash;
    case "doge":
      return Coin.dogecoin;
    case "epic":
      return Coin.epicCash;
    case "xec":
      return Coin.eCash;
    case "eth":
      return Coin.ethereum;
    case "firo":
      return Coin.firo;
    case "xmr":
      return Coin.monero;
    case "nmc":
      return Coin.namecoin;
    case "part":
      return Coin.particl;
    case "sol":
      return Coin.solana;
    case "xlm":
      return Coin.stellar;
    case "xtz":
      return Coin.tezos;
    case "tltc":
      return Coin.litecoinTestNet;
    case "tbtc":
      return Coin.bitcoinTestNet;
    case "tbch":
      return Coin.bitcoincashTestnet;
    case "tfiro":
      return Coin.firoTestNet;
    case "tdoge":
      return Coin.dogecoinTestNet;
    case "wow":
      return Coin.wownero;
    case "xno":
      return Coin.nano;
    case "ban":
      return Coin.banano;
    case "txlm":
      return Coin.stellarTestnet;
    default:
      throw ArgumentError.value(
          ticker, "name", "No Coin enum value with that ticker");
