import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';

import '../providers/ui/preview_tx_button_state_provider.dart';
import '../themes/stack_colors.dart';
import '../utilities/assets.dart';
import '../utilities/constants.dart';
import '../utilities/enums/mwc_transaction_method.dart';
import '../utilities/util.dart';
import 'toggle.dart';

class MwcTxsMethodToggle extends ConsumerWidget {
  const MwcTxsMethodToggle({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    debugPrint("BUILD: $runtimeType");
    final isDesktop = Util.isDesktop;

    return Toggle(
      onValueChanged: (value) {
        ref.read(pSelectedMwcTransactionMethod.notifier).state =
            value
                ? MwcTransactionMethod.mwcmqs
                : MwcTransactionMethod.slatepack;
      },
      isOn:
          ref.watch(pSelectedMwcTransactionMethod) ==
          MwcTransactionMethod.mwcmqs,
      onColor:
          isDesktop
              ? Theme.of(
                context,
              ).extension<StackColors>()!.rateTypeToggleDesktopColorOn
              : Theme.of(
                context,
              ).extension<StackColors>()!.rateTypeToggleColorOn,
      offColor:
          isDesktop
              ? Theme.of(
                context,
              ).extension<StackColors>()!.rateTypeToggleDesktopColorOff
              : Theme.of(
                context,
              ).extension<StackColors>()!.rateTypeToggleColorOff,
      decoration: BoxDecoration(
        borderRadius: BorderRadius.circular(
          Constants.size.circularBorderRadius,
        ),
      ),
      onIcon: Assets.svg.gear,
      onText: "Slatepack",
      offIcon: Assets.svg.radioSyncing,
      offText: "Automatic",
    );
  }
}
